library(shiny)

# Conversion functions
convert_sit_to_stand <- function(sit) {
  round( (sit  + 0.24)/1.14 , 2)
}

convert_stand_to_sit <- function(stand) {
  round(-0.24 + 1.14 * stand, 2)
}


 
 



ui <- fluidPage(
  tags$head(
    tags$style(HTML("
      .tab-content {
        margin-top: 30px;
      }
      .numeric-input-box {
        width: 100px;
      }
      .well-custom {
        background-color: #f9f9f9;
        padding: 20px;
        border-radius: 10px;
        box-shadow: 2px 2px 5px #ccc;
      }
      .details-panel {
        background-color: #eef;
        padding: 20px;
        border-radius: 10px;
        box-shadow: 2px 2px 5px #aaa;
      }
    "))
  ),
  titlePanel("Sit-Stop ↔ Stand-Stop Converter"),
  tabsetPanel(
    tabPanel("Converter",
             fluidRow(
               column(4, offset = 4,
                      div(class = "well-custom",
                          # h3("Sit-to-stand Test"),
                          numericInput("sit_stop", "Sit-Stop (s):", value = NA, min = 0, step = 0.05, width = "150px"),
                          numericInput("stand_stop", "Stand-Stop (s):", value = NA, min = 0, step = 0.05, width = "150px")
                      )
               )
             ),
             
             # br(),
             # br(),
             # br(),
             # 
             # fluidRow(
             #   column(4, offset = 4,
             #          div(class = "well-custom",
             #              # h3("Gait Speed Test"),
             #              numericInput("sit_stop", "Standing-start (s):", value = NA, min = 0, step = 0.05, width = "150px"),
             #              numericInput("stand_stop", "Moving-Start (s):", value = NA, min = 0, step = 0.05, width = "150px")
             #          )
             #   )
             # )
             
             
    ),
    
 
 
    
    
    tabPanel("Details",
             fluidRow(
               column(8, offset = 2,
                      div(class = "details-panel",
                          h4("Conversion Equations"),
                          p("Sit-Stop = -0.24 + 1.14 × Stand-Stop"),
                          p("Stand-Stop = 0.21 + 0.87 × Sit-Stop"),
                          br(),
                          strong("Reference:"),
                          br(),
                          p("Lim SQ, Pua YH et al. Sit-stop vs Stand-Stop Sit-to-stand timing conversion (Manuscript in Preparation)")
                      )
               )
             )
    )
  )
)

server <- function(input, output, session) {
  observeEvent(input$sit_stop, {
    if (!is.na(input$sit_stop) && input$sit_stop >= 0) {
      updateNumericInput(session, "stand_stop", value = convert_sit_to_stand(input$sit_stop))
    }
  })
  
  observeEvent(input$stand_stop, {
    if (!is.na(input$stand_stop) && input$stand_stop >= 0) {
      updateNumericInput(session, "sit_stop", value = convert_stand_to_sit(input$stand_stop))
    }
  })
}

shinyApp(ui, server)
